FROM ${database}.IBTABLE 
WHERE bookcode = #{bookcode}

SELECT tbcsr, tbdsr 
FROM sky.${dbname} 
WHERE tbsbook = #{tbsbook} 
	AND tbmgubn  >=  #{tbmgubn} 
ORDER BY tbmgubn ASC

SELECT tbcsr, tbdsr 
FROM sky.${dbname}
WHERE tbsbook = #{tbsbook} 
	AND tbmgubn  >  0 
	AND tbmgubn  <  #{tbmgubn} 
ORDER BY tbmgubn ASC

SELECT SUM(S1QNTY) AS s1qnty
FROM sky.${dbname}
WHERE s1book LIKE #{s1book} 
	AND s1gubn LIKE 'C' 
	AND s1ilja  <=  #{s1ilja}

SELECT SUM(s1qnty) AS s1qnty 
FROM sky.${dbname} 
WHERE s1book LIKE #{s1book} 
	AND s1gubn = #{tmp} 
	AND s1ilja  <=  #{s1ilja}

SELECT SUM(s1qnty) AS s1qnty 
FROM sky.${dbname} 
WHERE s1book LIKE #{s1book} 
	AND s1gubn = #{tmp}
	AND s1ilja  >=  #{bdate}

SELECT tbcsr, tbdsr 
FROM sky.${dbname} 
WHERE tbsbook = #{tbsbook} 
	AND tbmgubn  >=  #{tmp} 
ORDER BY tbmgubn ASC

SELECT tbcsr, tbdsr 
FROM ${dbname} 
WHERE tbsbook = #{tbsbook} 
	AND tbmgubn  >  0 
	AND tbmgubn  <  #{tbmgubn} 
ORDER BY tbmgubn ASC

SELECT sum(s1qnty) AS psum 
FROM sky.${dbname}
WHERE s1ilja  >=  #{s1ilja} 
	AND s1gubn = #{s1gubn} 
	AND s1book = #{s1book}

SELECT sum(s1qnty) AS psum 
FROM sky.${dbname} 
WHERE s1gubn = #{s1gubn} 
	AND s1book = #{tbsbook}

SELECT sum(s1qnty) AS psum 
