mapper.select:
SELECT COUNT(uid) as count
FROM tmplist
WHERE signdate = #{signdate}

SELECT *
FROM tmplist
WHERE signdate = #{signdate}
ORDER BY bcheck ASC, uid ASC 
LIMIT #{lm_s}, #{lm_t}

SELECT * 
FROM TMPLIST 
WHERE signdate  >=  #{date1}
	AND signdate  <=  #{date2}
ORDER BY bcheck ASC, uid ASC

SELECT *
FROM tmplist
WHERE uid = #{uid}

SELECT SBBOOK
FROM kscbook0
WHERE SBBOOK = #{bcode}

SELECT *
FROM kscbook0
WHERE SBBOOK = #{bcode}

SELECT *
FROM kswjijl0
WHERE wjcode = #{wyjijl}

SELECT *
FROM yeo
WHERE bu  <=  #{bnum}
	AND ty = 0
	AND do = #{wycolo}
ORDER BY bu DESC

SELECT COUNT(uid)
FROM kswdesu0
WHERE WDBOOK = #{bcode}
	AND WDBOO9 = #{wyboo9}

SELECT *
FROM KSWDESU0
WHERE WDBOOK = #{bcode}
	AND WDBOO9 = #{wyboo9}
ORDER BY WDDESU ASC

SELECT uid, signdate, bnum 
FROM TMPLIST 
WHERE bcode LIKE #{bcode} 
	AND uid  <  #{listid} 
ORDER BY signdate DESC

SELECT uid, listid 
FROM TMPLIST2 
WHERE listid = #{uid}

SELECT * 
FROM TMPLIST2 
WHERE bdate  >=  #{bdate1}
	AND bdate  <  #{bdate2}

SELECT wcemail, wcname 
FROM KSWCUST0
WHERE wccode = #{ccode}

SELECT a.m1, a.m2, a.m3, a.m4, a.m5, a.m6, a.m7, a.m8, a.m9, a.listid, a.bucode, a.pwan, a.iwan, b.wcname
FROM TMPLIST2 AS a, KSWCUST0 AS b
WHERE a.bdate  >=  #{bdate1}
	AND a.bdate  <  #{bdate2}
	AND a.m3 = b.wccode 
ORDER BY a.bdate ASC, a.uid ASC

SELECT wcyakc 
FROM KSWCUST0 
WHERE wccode = #{wccode}

		SELECT a.uid, a.bdate, a.m1, a.m3, a.bucode, c.bname, c.bcode, 
			(SELECT wcname FROM KSWCUST0 WHERE wccode = m1) AS temp1,
   			(SELECT wcname FROM KSWCUST0 WHERE wccode = m3) AS temp2
		FROM TMPLIST2 AS a, TMPLIST AS c, KSWCUST0 AS d
		WHERE a.listid=c.uid 
			AND a.bdate  >=  #{bdate1}  
			AND a.bdate  <  #{bdate2}
			AND d.wccode = a.m1 
		ORDER BY a.bdate ASC, a.uid ASC
		LIMIT #{lm_s}, #{lm_t}

    SELECT uid, jm, yb, gb, dae, colo, t4bigo 
	FROM TMPLIST4 
	WHERE listid = #{uid}
		AND bu = #{bucode}
		AND gb LIKE '%본문%' 
	ORDER BY dae ASC, uid ASC

SELECT jm, yb
FROM TMPLIST3 
